import React, {Component} from 'react';
import SearchBox from './SearchBox';
import {robots} from './robots';
import CardList from './CardList';

class App extends Component {

    constructor () {
        super()
        this.state = {
            robots: robots,
            searchField: ''
        }
    }

    onChangeSearch (event) {
        this.setState({searchField: event.value.input})

    }


    render () {
        const filteredArray = this.state.robots.filter(robot => {
            return this.state.robots.toLowerCase().includes(this.state.searchField.toLowerCase())
        })

        return (
            <div className='tc'>
                <h1>ROBOFRIENDS</h1>
                <SearchBox searchInput={this.searchByName}/>
                <CardList robots={filteredArray}/>
            </div>
        );
    }

}


export default App;
