import React, {Component} from 'react';
import SearchBox from './SearchBox';
import {robots} from './robots';
import CardList from './CardList';

class App extends Component {

    constructor () {
        super()
        this.state = {
            robots: robots,
            searchField: ''
        }
    }

    searchByName(event)  {
        SearchBox.value = this.state.searchField;
    }

    render() {
        return (
            <div className='tc'>
                <h1>ROBOFRIENDS</h1>
                <SearchBox />
                <CardList robots={this.state.robots} />
            </div>
        );
    }
}


export default App;
